mapper.update:
update file_info

	biztype =#{biztype},
	bizid = #{todoId}

where id in (
#{fileId}
 )

mapper.select:
SELECT DATE_FORMAT(t.updateTime,'%Y-%m-%d %H:%i:%s') updateTime, u.nickname, u.username, p.deptname FROM flow_todo t LEFT JOIN sys_user u ON t.auditby = u.id LEFT JOIN t_dept p ON u.deptid = p.id where t.id in (
#{fileId}
)
