mapper.select:
select t.*
from zc_bf_item t
where t.id = #{id}

select t.*
,t_creator.nickname as creator
from zc_bf_item t
LEFT OUTER JOIN sys_user t_creator ON t.createby=t_creator.id

${params.orderBy}
limit #{offset}, #{limit}

mapper.update:
		update zc_bf_item t


				t.shb_status = #{status},t.shUsername = #{username},t.shDeptname=#{deptname}


				t.cwb_status = #{status},t.cwUsername = #{username},t.cwDeptname=#{deptname}


				t.del = #{status},t.shb_status = 3,


                t.status = #{status}


		where t.id in
		(

                #{fileId}

		)

update zc_bf_item t
set t.zcbfCwItemid = #{zcBfCwitemId}
where t.id in
(

	#{fileId}

)

mapper.select:
SELECT t.id, t.zc_bf_id zcbfid, t.status, t.fileName, t.fileUrl, t.identifyContent, t.identifyFileName, t.identifyFileUrl, t.bz, t.shb_status shbStatus, t.cwb_status cwbStatus, t.shUsername, t.shDeptname, t.cwUsername, t.cwDeptname, t.del, zb.bf_category category, u1.nickname uname, p3.deptname udeptname, c.zc_codenum zcCodenum, c.zc_name zcName, g.name categoryName, c.specification, c.model, c.unit, c.use_status, c.zc_from zcfrom, c.card_num cardNum, c.zcCategoryName, DATE_FORMAT(c.start_use_time,'%Y-%m-%d') startusetime, c.lave_time lavetime, c.original_value originalvalue, c.netvalue, c.id zcId, c.sy_dept_id syDeptId, c.gl_dept_id glDeptId, c.epcid, u.nickname, d1.deptname sydeptname, d2.deptname gldeptname, c.store_address storeaddress, c.inspect_time inspecttime, c.warrantyperiod FROM zc_bf_item t left join zc_bf zb on zb.id = t.zc_bf_id LEFT JOIN v_zc_info c ON t.zc_id = c.id LEFT JOIN t_dept d1 ON c.sy_dept_id = d1.id LEFT JOIN t_dept d2 ON c.gl_dept_id = d2.id LEFT JOIN sys_user u ON c.user_id = u.id LEFT JOIN zc_category g ON c.zc_category_id = g.id left join sys_user u1 on t.apply_user_id = u1.id left join t_dept p3 on u1.deptid = p3.id where t.id in (
#{fileId}
)

SELECT t.id, t.status, t.flow_todo_id flowTodoId, t.flow_item_id flowItemId, t.auditby, t.sendby, p.zc_bf_id zcbfid, p.bz, p.shb_status shbStatus, p.cwb_status cwbStatus, p.status itemStatus, p.fileName, p.fileUrl, p.identifyContent, p.identifyFileName, p.identifyFileUrl, p.damagedFileName, p.damagedFileUrl, p.del, c.epcid, c.zc_codenum zcCodenum, c.zc_name zcName, g.name categoryName, c.specification, c.model, c.unit, c.use_status, DATE_FORMAT(c.start_use_time,'%Y-%m-%d %h:%m:%s') startusetime, c.remainingperiod, c.original_value originalvalue, c.netvalue, u.nickname, d1.deptname sydeptname, d2.deptname gldeptname, c.store_address storeaddress, c.inspect_time inspecttime, c.warrantyperiod FROM flow_todo_item t LEFT JOIN zc_bf_item p ON t.flow_item_id = p.id LEFT JOIN zc_info c ON p.zc_id = c.id LEFT JOIN t_dept d1 ON c.sy_dept_id = d1.id LEFT JOIN t_dept d2 ON c.gl_dept_id = d2.id LEFT JOIN sys_user u ON c.user_id = u.id LEFT JOIN zc_category g ON c.zc_category_id = g.id
t.flow_todo_id = #{todoId}
and p.del = 0

select count(1) from zc_bf_item t where (t.shb_status != 1 or t.shb_status is null ) and t.del = 0
and t.id in
(

    #{fileId}

)

        select t.*,c.epcid,c.zc_name zcName from zc_bf_item t
		left join zc_info c on c.id = zc_id
        where t.id in
        (

            #{fileId}

        )

SELECT t.zc_bf_id zcBfId, t.sy_dept_id syDeptId, t.gl_dept_id glDeptId FROM (SELECT zc_bf_id,sy_dept_id,gl_dept_id FROM zc_bf_item t WHERE YEAR(t.create_time) = YEAR(now()) AND t.cwb_status = 1 and t.del = 0 and t.create_time < #{updateTime} GROUP BY t.sy_dept_id) t where t.sy_dept_id in (
#{fileId}
)

mapper.update:
update zc_bf_item set identifyFileName = #{name},identifyFileUrl = #{url}
where id in
(

	#{fileId}

)

mapper.select:
SELECT DATE_FORMAT(t.update_time,'%Y-%m-%d %H:%i:%s') updateTime, u.nickname, u.username, p.deptname FROM zc_bf_item t LEFT JOIN sys_user u ON u.id = t.apply_user_id LEFT JOIN t_dept p ON p.id = t.sy_dept_id where t.id in (
#{fileId}
)
