mapper.select:
select t.*
,t_creator.nickname as creator
from zc_bf t
LEFT OUTER JOIN sys_user t_creator ON t.create_by=t_creator.id

${params.orderBy}
limit #{offset}, #{limit}

		SELECT
			t.id,
			DATE_FORMAT(t.create_time,'%Y-%m-%d %H:%i') createtime,
			t.bf_des bfdes,
			t.bz,
			t.status,
			f.id flowid,
			f.flowname,
			s.id stepid,
			s.stepname,
			u.id userid,
			u.nickname,
			u.username,
			d.id deptid,
			d.deptname
		FROM
			zc_bf t
		LEFT JOIN sys_user u ON t.apply_user_id = u.id
		LEFT JOIN t_dept d ON t.apply_dept_id = d.id
		LEFT JOIN flow f ON t.flowid = f.id
		LEFT JOIN flowstep s ON t.stepid = s.id


				and t.id = #{params.id}


				and t.apply_user_id = #{params.applyUserId}


				and t.apply_dept_id = #{params.applyDeptId}


				and t.zc_ids = #{params.zcIds}


				and t.bf_des = #{params.bfDes}


				and t.bz = #{params.bz}


				and t.flowid = #{params.flowid}


				and t.stepid = #{params.stepid}


				and t.status = #{params.status}


				and t.del = #{params.del}


				and t.create_by = #{params.createBy}


				and t.update_by = #{params.updateBy}


				and t.create_time = #{params.createTime}


				and t.update_time = #{params.updateTime}


				and u.nickname like concat('%',#{params.nickname},'%')


        order by t.create_time
        limit #{offset}, #{limit}

mapper.update:
update zc_bf t


		t.status = #{status}


where t.id in
(

	#{fileId}

)

mapper.select:
SELECT
	count(t.id)
FROM
( SELECT t.id,t.zcbfCwItemid,t.apply_user_id,t.zc_id,t.create_time,t.update_time,t.zc_bf_id,t.bz,fileName,
fileUrl,identifyContent,identifyFileName,identifyFileUrl
FROM zc_bf_item t
WHERE t.cwb_status = 1 AND t.del = 0
GROUP BY t.zc_id ) t
LEFT JOIN zc_info z on t.zc_id = z.id
LEFT JOIN t_dept p1 ON z.sy_dept_id = p1.id
LEFT JOIN t_dept p2 ON z.gl_dept_id = p2.id
LEFT JOIN zc_bf b ON b.id = t.zc_bf_id
LEFT JOIN zc_bf_cwitem zcw ON zcw.id = t.zcbfCwItemid
left join  zc_category as ca on z.zc_category_id = ca.id


		t.apply_user_id = #{params.applyUserId}


		zcw.codeNum = #{params.codeNum}


		and z.zc_category_id = #{params.zcCategoryId}


		and  z.start_use_time >= #{params.sdate} 


		and  z.start_use_time < DATE_FORMAT(DATE_ADD(#{params.edate},INTERVAL 1 DAY),'%Y-%m-%d') 


		and z.gl_dept_id = #{params.glDeptId}


		and z.sy_dept_id = #{params.syDeptId}


		and z.zc_name like concat('%',#{params.zcName},'%')


		and z.original_value = #{params.originalValue}


		and z.zc_codenum = #{params.zcCodenum}


		and z.epcid = #{params.epcid}

SELECT zcw.codeNum, z.epcid, z.zc_codenum zcCodenum, z.zc_name zcName, ca.name , b.bf_des bfdes, b.bz bfbz, t.bz bfReason, t.fileName, t.fileUrl, t.identifyContent, t.identifyFileName, t.identifyFileUrl, DATE_FORMAT(t.create_time,'%Y-%m-%d %H:%m') applyTime, DATE_FORMAT(t.update_time,'%Y-%m-%d %H:%m') finishTime, z.specification, z.model, p1.deptname sydeptname, p2.deptname gldeptname, z.store_address storeAddress, z.inspect_time inspectTime, z.warrantyperiod, z.zc_from zcFrom, z.original_value originalValue, z.lj_zhejiu ljZhejiu, z.bn_zhejiu bnZhejiu, z.netvalue, z.jzzb, z.net, z.net_residual_rate netResidualRate, z.net_residual_value netResidualValue, z.start_use_time startUseTime, z.use_months usemonths, z.have_count haveCount FROM ( SELECT t.id,t.zcbfCwItemid,t.apply_user_id,t.zc_id,t.create_time,t.update_time,t.zc_bf_id,t.bz,fileName, fileUrl,identifyContent,identifyFileName,identifyFileUrl FROM zc_bf_item t WHERE t.cwb_status = 1 AND t.del = 0 GROUP BY t.zc_id ) t LEFT JOIN zc_info z on t.zc_id = z.id LEFT JOIN t_dept p1 ON z.sy_dept_id = p1.id LEFT JOIN t_dept p2 ON z.gl_dept_id = p2.id LEFT JOIN zc_bf b ON b.id = t.zc_bf_id LEFT JOIN zc_bf_cwitem zcw ON zcw.id = t.zcbfCwItemid left join zc_category as ca on z.zc_category_id = ca.id
t.apply_user_id = #{params.applyUserId}
zcw.codeNum = #{params.codeNum}
and z.zc_category_id = #{params.zcCategoryId}
and z.start_use_time >= #{params.sdate}
and z.start_use_time < DATE_FORMAT(DATE_ADD(#{params.edate},INTERVAL 1 DAY),'%Y-%m-%d')
and z.gl_dept_id = #{params.glDeptId}
and z.sy_dept_id = #{params.syDeptId}
and z.zc_name like concat('%',#{params.zcName},'%')
and z.original_value = #{params.originalValue}
and z.zc_codenum = #{params.zcCodenum}
and z.epcid = #{params.epcid}
ORDER BY t.zcbfCwItemid, t.create_time DESC limit #{offset}, #{limit}
