mapper.select:
select t.*
,t_creator.nickname as creator
from zc_buy_item t
LEFT OUTER JOIN sys_user t_creator ON t.createby=t_creator.id

${params.orderBy}
limit #{offset}, #{limit}

SELECT t.id, b.buyNum, t.zc_buy_id zcBuyId, t.name, t.model, t.unit, t.brand, t.price, t.totalPrice, t.supplier_name supplierName, t.use_des useDes, t.status, t.num, IFNULL(t.price,0)*t.num totalPrice, t.fileName, t.fileUrl, t.zc_bz, DATE_FORMAT(t.arrive_time,'%Y-%m-%d') arriveTime, t.zc_bz zcBz, p.deptname gldeptname FROM zc_buy_item t LEFT JOIN t_dept p ON t.gl_dept_id = p.id LEFT JOIN zc_buy b ON b.id = t.zc_buy_id
t.zc_buy_id = #{zcBuyId}
and t.cwb_status = 1 and t.del = 0

SELECT count(t.id) FROM zc_buy_item t LEFT JOIN zc_buy z ON t.zc_buy_id = z.id LEFT JOIN t_dept p1 ON t.gl_dept_id = p1.id LEFT JOIN t_dept p2 ON z.sy_dept_id = p2.id LEFT JOIN sys_user u ON u.id = z.apply_user_id
t.name like concat('%',#{params.name},'%')
and t.use_des like concat('%',#{params.useDes},'%')
and z.create_time >= #{params.sdate}
and z.create_time < DATE_FORMAT(DATE_ADD(#{params.edate},INTERVAL 1 DAY),'%Y-%m-%d')
and t.model like concat('%',#{params.model},'%')
and t.brand like concat('%',#{params.brand},'%')
and t.price like concat('%',#{params.price},'%')
and t.gl_dept_id = #{params.glDeptId}
and z.sy_dept_id = #{params.syDeptId}
and z.buyNum = #{params.buyNum}
and t.cwb_status = 1 and t.del = 0

SELECT z.buyNum, t.name, t.model, t.unit, t.brand, IFNULL(t.price,0) price, t.num, IFNULL(t.price,0)*t.num totalPrice, t.supplier_name supplierName, t.use_des useDes, t.zc_bz, DATE_FORMAT(t.create_time,'%Y-%m-%d %H:%i:%s') applyTime, DATE_FORMAT(t.update_time,'%Y-%m-%d %H:%i:%s') finishTime, u.username, u.nickname applyNickname, p2.deptname sydeptname, p1.deptname gldeptname FROM zc_buy_item t LEFT JOIN zc_buy z ON t.zc_buy_id = z.id LEFT JOIN t_dept p1 ON t.gl_dept_id = p1.id LEFT JOIN t_dept p2 ON z.sy_dept_id = p2.id LEFT JOIN sys_user u ON u.id = z.apply_user_id
t.name like concat('%',#{params.name},'%')
and t.use_des like concat('%',#{params.useDes},'%')
and z.create_time >= #{params.sdate}
and z.create_time < DATE_FORMAT(DATE_ADD(#{params.edate},INTERVAL 1 DAY),'%Y-%m-%d')
and t.model like concat('%',#{params.model},'%')
and t.brand like concat('%',#{params.brand},'%')
and t.price like concat('%',#{params.price},'%')
and t.gl_dept_id = #{params.glDeptId}
and z.sy_dept_id = #{params.syDeptId}
and z.buyNum = #{params.buyNum}
and t.cwb_status = 1 and t.del = 0
ORDER BY z.buyNum ASC,t.create_time DESC limit #{offset}, #{limit}
