mapper.select:
select t.*,d.deptName as checkDeptName,t_creator.nickname as creator
from zc_check t
left join t_dept d on d.id=t.check_dept_id
LEFT OUTER JOIN sys_user t_creator ON t.create_by=t_creator.id

${params.orderBy}
limit #{offset}, #{limit}

select.if:
-- 财务部 select b.deptname AS creator, e.deptname AS checkDeptName , d. `id`, d. `check_dept_id` as checkDeptId, d. `check_user_id` as checkUserId , d. `check_time` as checkTime , d. `total` , d.`status` , d.`result`, d. `del` , d.`bz` , d.`create_by` as createBy, d.`update_by` as updateBy, d.`create_time` as createTime, d.`update_time` as updateTime, d.`check_times` as checkTimes, d. `re_check` as reCheck, d.`bh`, d.`profit` FROM t_dept AS b LEFT JOIN sys_user as c ON b.id = c.deptid LEFT JOIN zc_check AS d ON c.id = d.create_by LEFT JOIN t_dept AS e ON d.check_dept_id = e.id WHERE b.pid = #{params.pid} and d.create_by is not null
and d.status in
#{status}
and d.check_dept_id = #{params.checkDeptId}
and d.del = #{params.del}
and d.check_time >= #{params.sdate} and d.check_time <= #{params.edate}
and d.check_dept_id = #{params.syDept}
and d.create_by = #{params.glDept}
and d.profit = #{params.profit}
${params.orderBy} limit #{offset}, #{limit}

-- 其他的四个部门
( SELECT
b.deptname AS creator,
e.deptname AS checkDeptName,
