mapper.select:
select t.*
,t_creator.nickname as creator
from zc_deploy_item t
LEFT OUTER JOIN sys_user t_creator ON t.createby=t_creator.id

${params.orderBy}
limit #{offset}, #{limit}

SELECT t.id, t.zc_deploy deployId, t.status, t.bz, t.front_dept_status frontDeptStatus, t.back_dept_status backDeptStatus, t.cwb_status cwbStatus, t.frontUser, t.backUser, t.frontUsername, t.backUsername, c.id zcId, c.zc_codenum zcCodenum, c.zc_name zcName, g.name categoryName, c.specification, c.epcid, c.model, c.card_num cardNum, c.unit, c.use_status, c.zc_from zcfrom, DATE_FORMAT(c.start_use_time,'%Y-%m-%d %h:%m:%s') startusetime, c.lave_time lavetime, IFNULL(c.original_value,0) originalvalue, c.netvalue, c.id zcId, c.sy_dept_id syDeptId, c.gl_dept_id glDeptId, u.nickname, d1.deptname sydeptname, d2.deptname gldeptname, d3.deptname backdeptname, c.store_address storeaddress, c.inspect_time inspecttime, c.warrantyperiod, c.remainingperiod FROM zc_deploy_item t LEFT JOIN zc_info c ON t.zc_id = c.id LEFT JOIN t_dept d1 ON t.front_dept_id = d1.id LEFT JOIN t_dept d2 ON t.gl_dept_id = d2.id LEFT JOIN t_dept d3 ON t.back_dept_id = d3.id LEFT JOIN sys_user u ON c.user_id = u.id LEFT JOIN zc_category g ON c.zc_category_id = g.id
t.zc_deploy = #{zcDeployId}
and t.del = 0 and t.cwb_status = 1

mapper.update:
update zc_deploy_item t


		t.front_dept_status = #{status},t.frontUser = #{userId},t.frontUsername = #{nickname}


		t.back_dept_status = #{status},t.backUser = #{userId},t.backUsername = #{nickname}


		t.cwb_status = #{status}


		t.del = #{status}


where t.id in
(

	#{fileId}

)
