mapper.select:
select t.*
,t_creator.nickname as creator
from zc_deploy t
LEFT OUTER JOIN sys_user t_creator ON t.createby=t_creator.id

${params.orderBy}
limit #{offset}, #{limit}

SELECT t.id, DATE_FORMAT(t.create_time,'%Y-%m-%d %H:%m') createtime, t.back_dept_id backDeptId, t.description, t.bz, t.status, f.id flowid, f.flowname, s.id stepid, s.stepname, u.id userid, u.nickname, u.username, d.id deptid, d.deptname, d1.deptname backDeptname FROM zc_deploy t LEFT JOIN sys_user u ON t.apply_user_id = u.id LEFT JOIN t_dept d ON t.apply_dept_id = d.id LEFT JOIN t_dept d1 ON t.back_dept_id = d1.id LEFT JOIN flow f ON t.flowid = f.id LEFT JOIN flowstep s ON t.stepid = s.id
and u.nickname like concat('%',#{params.nickname},'%')
${params.orderBy} limit #{offset}, #{limit}

		SELECT
			count(t.id)
		FROM zc_deploy_item t
		LEFT JOIN zc_info z on t.zc_id = z.id
		LEFT JOIN t_dept p1 ON t.gl_dept_id = p1.id
		LEFT JOIN t_dept p2 ON t.front_dept_id = p2.id
		LEFT JOIN t_dept p3 ON t.back_dept_id = p3.id
		LEFT JOIN zc_deploy b ON b.id = t.zc_deploy


				t.apply_user_id = #{params.applyUserId}


                and b.deployNum = #{params.deployNum}


				and t.front_dept_id = #{params.frontDeptId}


				and t.back_dept_id = #{params.backDeptId}


				and z.zc_category_id = #{params.zcCategoryId}


				and  z.start_use_time >= #{params.sdate} 


				and  z.start_use_time < DATE_FORMAT(DATE_ADD(#{params.edate},INTERVAL 1 DAY),'%Y-%m-%d') 


				and t.gl_dept_id = #{params.glDeptId}


				and z.zc_name like concat('%',#{params.zcName},'%')


				and z.original_value = #{params.originalValue}


				and z.zc_codenum = #{params.zcCodenum}


				and z.epcid = #{params.epcid}

			and t.cwb_status = 1 and t.del = 0

SELECT b.deployNum, t.bz bfReason, DATE_FORMAT(t.create_time,'%Y-%m-%d %H:%m') applyTime, DATE_FORMAT(t.update_time,'%Y-%m-%d %H:%m') finishTime, p1.deptname gldeptname, p2.deptname frontDeptname, p3.deptname backDeptname, z.specification, z.model, z.zc_codenum zcCodenum, z.zc_name zcName, z.epcid, z.store_address storeAddress, z.inspect_time inspectTime, z.warrantyperiod, z.zc_from zcFrom, z.original_value originalValue, z.lj_zhejiu ljZhejiu, z.bn_zhejiu bnZhejiu, z.netvalue, z.jzzb, z.net, z.net_residual_rate netResidualRate, z.net_residual_value netResidualValue, z.start_use_time startUseTime, z.use_months usemonths, z.have_count haveCount, z.use_status FROM zc_deploy_item t LEFT JOIN zc_info z on t.zc_id = z.id LEFT JOIN t_dept p1 ON t.gl_dept_id = p1.id LEFT JOIN t_dept p2 ON t.front_dept_id = p2.id LEFT JOIN t_dept p3 ON t.back_dept_id = p3.id LEFT JOIN zc_deploy b ON b.id = t.zc_deploy
t.apply_user_id = #{params.applyUserId}
and b.deployNum = #{params.deployNum}
and t.front_dept_id = #{params.frontDeptId}
and t.back_dept_id = #{params.backDeptId}
and z.zc_category_id = #{params.zcCategoryId}
and z.start_use_time >= #{params.sdate}
and z.start_use_time < DATE_FORMAT(DATE_ADD(#{params.edate},INTERVAL 1 DAY),'%Y-%m-%d')
and t.gl_dept_id = #{params.glDeptId}
and z.zc_name like concat('%',#{params.zcName},'%')
and z.original_value = #{params.originalValue}
and z.zc_codenum = #{params.zcCodenum}
and z.epcid = #{params.epcid}
and t.cwb_status = 1 and t.del = 0
ORDER BY b.deployNum ASC,t.create_time DESC limit #{offset}, #{limit}
